DROP TABLE IF EXISTS `tb_sentence`;

CREATE TABLE `tb_sentence` (
  `id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '短句',
  `author` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '作者',
  `book` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '书名',
  `show_times` int(10) unsigned NOT NULL COMMENT '展示次数',
  `create_user_id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `update_user_id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `cherish_time_tb_sentence_show_times_index` (`show_times`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

LOCK TABLES `tb_sentence` WRITE;

UNLOCK TABLES;


DROP TABLE IF EXISTS `tb_time`;

CREATE TABLE `tb_time` (
  `id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `type` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '类型',
  `user_id` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '用户ID',
  `color` varchar(64) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '颜色',
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '名称',
  `date` int(10) unsigned NOT NULL COMMENT '日期',
  `remark` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '备注',
  `create_user_id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `update_user_id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `cherish_time_tb_time_user_id_index` (`user_id`),
  KEY `cherish_time_tb_time_date_index` (`date`),
  KEY `cherish_time_tb_time_type_index` (`type`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


DROP TABLE IF EXISTS `tb_user`;

CREATE TABLE `tb_user` (
  `id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `open_id` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'open_id',
  `nick_name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '昵称',
  `sex` tinyint(3) unsigned NOT NULL COMMENT '性别',
  `city` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '城市',
  `province` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '省份',
  `country` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '国家',
  `avatar` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '头像',
  `create_user_id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `update_user_id` varchar(32) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `cherish_time_tb_user_open_id_index` (`open_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;
